func findBasement(_ instructions: String) -> Int {
    var floor = 0
    var position = 1
    for c in instructions {
        floor += (c == "(") ? 1 : -1
        if floor == -1 {
            break
        }
        position += 1
    }
    return position
}

func moveFloor(_ instructions: String) -> Int {
    return instructions.reduce(0) { floor, c in
        floor + ((c == "(") ? 1 : -1)
    }
}



